mapper.update:
UPDATE sys_role_resource_permission
PERMISSION_ID=#{permissionId}, RESOURCE_ID=#{resourceId}, CREATE_TIME=#{createTime}, ROLE_ID=#{roleId}, status=#{status},
WHERE id=#{id}

mapper.select:
SELECT 
  FROM sys_role_resource_permission
WHERE 1=1
    AND resource_id=#{resourceId}
    AND permission_id=#{permissonId}
    AND role_id=#{roleId}
    AND status = 1

mapper.update:
UPDATE
  sys_role_resource_permission
SET status=#{status}
WHERE role_id=#{roleId}
